import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const Experience = () => {
  const [selectedId, setSelectedId] = useState(null);

  // Sample data for experience items
  const items = [
    {
      id: 1,
      subtitle: 'Research Intern',
      title: 'Twimbit Pte Ltd',
      description:
        'Researched and contributed to articles on ChatGPT in Financial services, Buy now Pay Later (BNPL), and Customer Experience in Singapore Banks.',
    },
    {
      id: 2,
      subtitle: 'IT Intern',
      title: 'Jumbo Group of Restaurants',
      description:
        'Captured, validated, and tested UI/UX interface for a new mobile application. Converted over 100 physical recipes into their internal ERP system.',
    },
    {
      id: 3,
      subtitle: 'Intern',
      title: 'Ncode Consultant',
      description: 'Upload photos on website created for the client. Extensive use of Photoshop to edit photos.',
    },
  ];

  return (
    <div className="experience">
      <h2>Experience</h2>
      {items.map((item) => (
  <motion.div
    layoutId={item.id}
    onClick={() => setSelectedId(selectedId === item.id ? null : item.id)}
    key={item.id}
    className={`experience-item ${selectedId === item.id ? 'selected' : ''}`}
  >
    <motion.h5>{item.subtitle}</motion.h5>
    <motion.h2>{item.title}</motion.h2>
    {selectedId === item.id && (
      <motion.p className="experience-description">{item.description}</motion.p>
    )}
  </motion.div>
))}


      <AnimatePresence>
        {selectedId !== null && (
          <motion.div layoutId={selectedId} className="selected-experience">
            <motion.h5>{items[selectedId - 1].subtitle}</motion.h5>
            <motion.h2>{items[selectedId - 1].title}</motion.h2>
            <motion.p className="experience-description">{items[selectedId - 1].description}</motion.p>
            <motion.button onClick={() => setSelectedId(null)}>Close</motion.button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default Experience;
