import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import "./experience.css";
import 'bootstrap/dist/css/bootstrap.min.css';

const Experience = () => {
  const [selectedTab, setSelectedTab] = useState(null);

  const tabs = [
    {
      label: "Twimbit Pte Ltd",
      content: (
        <div>
          <div>
            <h4>Job Title: Research Intern</h4>
          </div>
          <div>
          <ul>
                      <li>Researched and contributed to articles on ChatGPT in Financial services, Buy now Pay Later (BNPL) and Customer Experience in Singapore Banks.</li>
                      <li>Support in other ad hoc tasks such as converting slides & presentations into blog content and testing new ChatGPT chatbot in Twimbit website.</li>
                      <li>Host monthly challenge event twice with fellow intern and contributed to brainstorming ideas for solutions to monthly challenges.</li>
                    </ul>
          </div>

          {/* <img src="url_to_image" alt="Image Description" /> */}
        </div>
      ),
    },
    {
      label: "Jumbo Group of Restaurants Pte Ltd",
      content: "This is the content for Experience 2.",
    },
    {
      label: "Ncode Consultant Ptd Ltd",
      content: "This is the content for Experience 3.",
    },
  ];

  return (
    <div className="window">
      <nav>
        <ul>
          {tabs.map((item) => (
            <li
              key={item.label}
              className={item === selectedTab ? "selected" : ""}
              onClick={() => setSelectedTab(item)}
            >
              {item.label}
              {item === selectedTab ? (
                <motion.div className="underline" layoutId="underline" />
              ) : null}
            </li>
          ))}
        </ul>
      </nav>
      <main>
        <AnimatePresence wait>
          <motion.div
            key={selectedTab ? selectedTab.label : "empty"}
            initial={{ y: 10, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            exit={{ y: -10, opacity: 0 }}
            transition={{ duration: 0.2 }}
          >
            {selectedTab ? selectedTab.content : "Select an experience."}
          </motion.div>
        </AnimatePresence>
      </main>
    </div>
  );
};

export default Experience;
