import React, { useState } from 'react';
import AccordionWrapper from './ProjectWrapper';
import AccordionItem from './ProjectItem';
import './Projects.css';

const Projects = () => {
  const [activeCategory, setActiveCategory] = useState('school');

  const schoolProjects = [
    {
      "title": "Final Year Project",
      "description": "Final Year Project Description",
      "github": "https://github.com/yourusername/final-year-project"
    },
    {
      "title": "Object Oriented Programming",
      "description": "OOP Project Description",
      "github": "https://github.com/yourusername/oop-project"
    },
    {
      "title": "Software Project Management",
      "description": "SPM Project Description",
      "github": "https://github.com/yourusername/spm-project"
    },
    {
      "title": "Enterprise Solution Development",
      "description": "ESD Project Description",
      "github": "https://github.com/yourusername/esd-project"
    }
  ];

  const personalProjects = [
    {
      "title": "Personal Portfolio Website",
      "description": "Portfolio Description",
      "github": "https://github.com/htlee1999/HTportfolio"
    },
    {
      "title": "Cristiano Ronaldo Stats Visualisation",
      "description": "Cristiano Ronaldo Description",
      "github": "https://github.com/htlee1999/CR7Stats"
    },
    {
      "title": "Fun Run website ",
      "description": "Task Manager Description",
      "github": "https://github.com/yourusername/task-manager"
    }
  ];

  const formatDescription = (description, github) => {
    return `
      <div class="project-content">
        <p>${description}</p>
        <a href="${github}" target="_blank" rel="noopener noreferrer" class="github-link">
          <span class="github-icon">
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><path d="M15 22v-4a4.8 4.8 0 0 0-1-3.5c3 0 6-2 6-5.5.08-1.25-.27-2.48-1-3.5.28-1.15.28-2.35 0-3.5 0 0-1 0-3 1.5-2.64-.5-5.36-.5-8 0C6 2 5 2 5 2c-.3 1.15-.3 2.35 0 3.5A5.403 5.403 0 0 0 4 9c0 3.5 3 5.5 6 5.5-.39.49-.68 1.05-.85 1.65-.17.6-.22 1.23-.15 1.85v4"/><path d="M9 18c-4.51 2-5-2-7-2"/></svg>
          </span>
          View on GitHub
        </a>
      </div>
    `;
  };

  return (
    <div className="App">
      <div className="content">
        <div className="app-description">
          <h1>Projects</h1>
        </div>

        <div className="category-tabs">
          <button 
            className={`tab-button ${activeCategory === 'school' ? 'active' : ''}`}
            onClick={() => setActiveCategory('school')}
          >
            School Projects
          </button>
          <button 
            className={`tab-button ${activeCategory === 'personal' ? 'active' : ''}`}
            onClick={() => setActiveCategory('personal')}
          >
            Personal Projects
          </button>
        </div>

        <AccordionWrapper>
          {(activeCategory === 'school' ? schoolProjects : personalProjects).map((item, index) => (
            <AccordionItem 
              key={index} 
              index={index} 
              title={item.title} 
              description={formatDescription(item.description, item.github)} 
            />
          ))}
        </AccordionWrapper>
      </div>
    </div>
  );
}

export default Projects;
