import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import 'bootstrap/dist/css/bootstrap.min.css';
import "./experience.css";

const Experience = () => {
  const [selectedId, setSelectedId] = useState(null);

  const items = [
    {
      id: 1,
      subtitle: "Research Intern",
      title: "Twimbit Pte Ltd",
      description:
        "Researched and contributed to articles on ChatGPT in Financial services, Buy now Pay Later (BNPL), and Customer Experience in Singapore Banks.",
    },
    {
      id: 2,
      subtitle: "IT Intern",
      title: "Jumbo Group of Restaurants",
      description:
        "Captured, validated, and tested UI/UX interface for a new mobile application. Converted over 100 physical recipes into their internal ERP system.",
    },
    {
      id: 3,
      subtitle: "Intern",
      title: "Ncode Consultant",
      description:
        "Upload photos on a website created for the client. Extensive use of Photoshop to edit photos.",
    },
  ];

  return (
    <div className="experience container">
      <h2>Experience</h2>
      <div className="row">
        {items.map((item) => (
          <motion.div
            className={`col-md-4 experience-item card ${
              selectedId === item.id ? "selected" : ""
            }`}
            key={item.id}
            onClick={() => setSelectedId(selectedId === item.id ? null : item.id)}
          >
            <h5>{item.subtitle}</h5>
            <h2>{item.title}</h2>
            <AnimatePresence>
              {selectedId === item.id && (
                <motion.p className="experience-description">
                  {item.description}
                </motion.p>
              )}
            </AnimatePresence>
          </motion.div>
        ))}
      </div>

      <AnimatePresence>
        {selectedId !== null && (
          <motion.div className="selected-experience">
            <h5>{items[selectedId - 1].subtitle}</h5>
            <h2>{items[selectedId - 1].title}</h2>
            <motion.p className="experience-description">
              {items[selectedId - 1].description}
            </motion.p>
            <motion.button onClick={() => setSelectedId(null)} className="btn btn-primary">
              Close
            </motion.button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default Experience;
